  "id": "08549",
  "type": "ScenarioReference",
  "class": "Mythos",
  "tokens": {
    "front": {
      "Skull": {
        "description": "-X. X is the number of story cards in the victory display.",
        "modifier": -999
      },
      "Cultist": {
        "description": "-2. If you fail, place 1 of your clues on your location.",
